import React from 'react';
import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { useEffect, useState } from 'react';
import { detailsProduct } from '../Actions/productActions';
import { useDispatch, useSelector } from 'react-redux';
export default function ProductScreen(props) {


    // const products = data.products;



    const dispatch = useDispatch();



    const productDetails = useSelector((state) => state.productDetails);
    const { product } = productDetails;
    const productId = props.match.params.id;

    useEffect(() => {
        dispatch(detailsProduct(productId));
    }, [dispatch, productId]);
    const [qty, setQty] = useState(1);
    console.log(product);
    console.log(productId);
    if (!product) {
        return null;
    }


    const addToCartHandler = () => {
        props.history.push(`/cart/${productId}?qty=${qty}`);
    }





    return ( <
        div >

        <
        Link to = "/" > Back to result < /Link> <
        div className = "row" > < div className = "col-2" > < img src = { product.image }
        /> < /
        div > < div className = "col-1" > < ul > < li > < h1 > {
            product.name

        } < /
        h1 > < /li > <li>{product.price}$ < /li > < li > {

        } {
            product.nreviews
        }
        reviews <
        div className = "rating" >
        <
        span > <
        i className = {
            product.rating >=
            1 ?
            'fa fa-star' : product.rating >= .5 ?
                'fa fa-star-half-o' : 'fa fa-star-o'
        } > < /i> <
        i className = {
            product.rating >=
            2 ?
            'fa fa-star' : product.rating >= 1.5 ?
                'fa fa-star-half-o' : 'fa fa-star-o'
        } > < /i> <
        i className = {
            product.rating >= 3 ?
            'fa fa-star' : product.rating >=
                2.5 ?
                'fa fa-star-half-o' : 'fa fa-star-o'
        } > < /i> <
        i className = {
            product.rating >= 4 ?
            'fa fa-star' : product.rating >= 3.5 ?
                'fa fa-star-half-o' : 'fa fa-star-o'
        } > < /i> <
        i className = {
            product.rating >= 5 ?
            'fa fa-star' : product.rating >= 4.5 ?
                'fa fa-star-half-o' : 'fa fa-star-o'
        } > < /i> < /
        span >

        <
        /div>

        <
        /li > < li >{product.description} < /li > < /ul > < /div > < div className = "col-1" > < div className = "card card-body" > < ul > < li > < div className = "row" > <
        div > Price < /div> <
        div className = "price" > { product.price }
        $ < /div> < /
        div > < /li > < li >   <div className="row"> <
        div > status < /div> <
        div className = "status" >

        <
        div > {
            product.countInStock > 0 ? ( < span className = "success" > In stock < /span>): ( < span className="error" > Unavailable< /span > )
        }

        <
        /div>




        <
        /div> < /
        div > < /li >




        {
            product.countInStock > 0 && ( <
                >
                <
                li >
                <
                div className = "row" >
                <
                div > Qty < /div> <
                div >
                <
                select value = { qty }
                onChange = {
                    (e) => setQty(e.target.value)
                } > {
                    [...Array(product.countInStock).keys()].map(
                        (x) => ( <
                            option key = { x + 1 }
                            value = { x + 1 } > { x + 1 } <
                            /option>
                        )
                    )
                } <
                /select> < /
                div > <
                /div> < /
                li > <
                li >
                <
                button onClick = { addToCartHandler }
                className = "primary block" >
                Add to Cart <
                /button> < /
                li > <
                />
            )
        }



        <

        /ul > < /
        div > < /div >


        <
        /div >< /div >




    );
}
