import { Link } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import { useState } from 'react';
import axios from 'axios';
export default function CartScreen(props) {

const [Subtotal,setSubtotal]=useState(0);
    const [products, setProducts] = useState(null)
    const [flag, setFlag] = useState(true)
    useEffect((
    ) => {
    }, [products,Subtotal])
    if (flag) {
        axios.get(`/615b4d369e839e5116fcbe9d/cart`).then((res) => {
            setProducts(res.data)
            let total=0;
            for(let i=0;i<products.length;i++){
                total+=products[i].countInStock*products[i].price;
            }
            setSubtotal(total)
            setFlag(false)
        })
    }


    const deleteproduct = async (ID) => {
        await axios.delete(`/615b4d369e839e5116fcbe9d/cart/${ID
            }`).then(() => {
                setFlag(true)
            })
        // setFlag(true)
    }
    return (
        <
   div >
            <
   h1 > cart screen < /h1>
                <
   div className="row top" >
                    <
   div className="col-2" >
                        <
   h1 > Shopping Cart < /h1> <ul>



                                {

                                    (products
                                    ) ? products.map(product => {
                                        return (
                                            <li key={product.id}>



                                                <
   div className="row" >
                                                    <
                                                        img src={
                                                            product.image
                                                        }
                                                        className="small" />

                                                    < div className="row" >{product.name
                                                    }</div>

                                                    < div className="row" > < div > {
                                                        product.countInStock
                                                    } < /div>  < /div >

                                                        <
div className="row" > {
                                                                product.price

                                                            }
                                                            $ < /div>


                                                            <
div > < button onClick={() => deleteproduct(product.id)}
                                                                > delete < /button>< /div >




                                                                    <
/div >






                                                                    < /li >)}):<h1></h1>}






                                                                    <
li >
                                                                        <
h2 >
                                                                            Subtotal :{Subtotal}<
/h2> < /
li >



                                                                            <
button
