import React from 'react'
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { signin } from '../Actions/userActions';

import { useDispatch, useSelector } from 'react-redux';


export default function SigninScreen(props) {
    const localPassword = localStorage.getItem("password")
    const localEmail = localStorage.getItem("email")

    const [password, setPassword] = useState(localPassword ? JSON.parse(localPassword) : "")
    const [email, setEmail] = useState(localEmail ? JSON.parse(localEmail) : "")
    const [flag, setFlag] = useState(false)

    const redirect = props.location.search
        ? props.location.search.split('=')[1]
        : '/';
    useEffect(async () => {

        if (flag) {
             setFlag(false)
            await axios.post("/signin", {
                password: password,
                email: email
            }).then((data) => {
                if (data.data.message === "ok") {
                    localStorage.setItem("email", JSON.stringify(email))
                    localStorage.setItem("password", JSON.stringify(password))
                     props.history.push(redirect)             
                }
                else {
                    localStorage.setItem("email", "")
                    localStorage.setItem("password", "")
                }
                console.log(data.data.message)
                 
            })
        }
    }, [props.history, redirect, email, password, flag]);
    const login = async () => {
        setFlag(true)
    }

    return (<
            div >
        <
            form className="form"
        >

            <
            h1 > Sign in < /h1><
            div > <
            label htmlFor="email" >
                        Email address <
            /label>

                        <
                            input type="email"
                            id="email"
                            placeholder="enter email"
                            onChange={(e) => {
                                setEmail(e.target.value)
                            }}
                            required


                        />

                        <
            /div > <
            div > <
            label htmlFor="password" >
                                Password <
            /label>

                                <
                                    input type="password"
                                    id="password"
                                    placeholder="enter password"
                                    onChange={(e) => {
                                        setPassword(e.target.value)
                                    }}
                                    required
                                />

                                <

            /div >

                                <
            div >
                                    <
                                        label /
                                    >
                                    <
                                        button type="submit" onClick={login}
                                        className="primary "
                                    > sign in < /button >



                                        <
            /div> <
            div >
                                            <
                                                label /
                                            >
                                            <
            div > New customer ? < Link to="/register" > Create your account < /Link>

                                                    <
            /div>



                                                    <
            /div>




                                                    <
            /
            form > < /
            div >
                                                    )
    }
