import React from 'react'
import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';
import axios from 'axios';
import { signin } from '../Actions/userActions';

import { useDispatch, useSelector } from 'react-redux';


export default function SigninScreen(props) {
    const localPassword = localStorage.getItem("password")
    const localEmail = localStorage.getItem("email")

    const [password, setPassword] = useState(localPassword ? json.parse(localPassword) : "")
    const [email, setEmail] = useState(localEmail ? Js.parse(localEmail) : "")
    const redirect = props.location.search
        ? props.location.search.split('=')[1]
        : '/';


    const login = async () => {
        await axios.post("/signin", {
            password: password,
            email: email
        }).then((data) => {
            if (data.data.message === "ok") {
                localStorage.setItem("email", json.stringify(email))
                localStorage.setItem("password", json.stringify(password))
            }
            console.log(localStorage.getItem("password"))
        })

    }

    return (<
            div >
        <
            form className="form"
            onSubmit=''  >

            <
            h1 > Sign in < /h1><
            div > <
            label htmlFor="email" >
                        Email address <
            /label>

                        <
                            input type="email"
                            id="email"
                            placeholder="enter email"
                            onChange={(e) => {
                                setEmail(e.target.value)
                            }}
                            required


                        />

                        <
            /div > <
            div > <
            label htmlFor="password" >
                                Password <
            /label>

                                <
                                    input type="password"
                                    id="password"
                                    placeholder="enter password"
                                    onChange={(e) => {
                                        setPassword(e.target.value)
                                    }}
                                    required
                                />

                                <

            /div >

                                <
            div >
                                    <
                                        label /
                                    >
                                    <
                                        button type="submit"
                                    className = "primary " onClick={login}
             > sign in < /button >



                                    <
            /div> <
            div >
                                        <
                                            label /
                                        >
                                        <
            div > New customer ? < Link to="/register" > Create your account < /Link>

                                                <
            /div>



                                                <
            /div>




                                                <
            /
            form > < /
            div >
                                                )
    }
